import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Joke from './Joke';
import './JokeList.css';

function JokeList({ numJokesToGet = 5 }) {
  const [jokes, setJokes] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const seenJokes = useRef(new Set());

  useEffect(() => {
    getJokes();
  }, []); // Mimics componentDidMount

  async function getJokes() {
    try {
      let fetchedJokes = [];
      while (fetchedJokes.length < numJokesToGet) {
        let res = await axios.get("https://icanhazdadjoke.com", {
          headers: { Accept: "application/json" }
        });
        let { id, joke } = res.data;

        if (!seenJokes.current.has(id)) {
          seenJokes.current.add(id);
          fetchedJokes.push({ id, joke, votes: 0 });
        } else {
          console.log("duplicate found!");
        }
      }
      setJokes(fetchedJokes);
      setIsLoading(false);
    } catch (err) {
      console.error(err);
    }
  }

  function generateNewJokes() {
    setIsLoading(true);
    getJokes();
  }

  function vote(id, delta) {
    setJokes(jokes.map(j => j.id === id ? { ...j, votes: j.votes + delta } : j));
  }

  let sortedJokes = [...jokes].sort((a, b) => b.votes - a.votes);
  return (
    <div className="JokeList">
      {isLoading ? (
        <div className="loading"><i className="fas fa-4x fa-spinner fa-spin" /></div>
      ) : (
        <>
          <button className="JokeList-getmore" onClick={generateNewJokes}>
            Get New Jokes
          </button>
          {sortedJokes.map(joke => (
            <Joke text={joke.joke} key={joke.id} id={joke.id} votes={joke.votes} vote={vote} />
          ))}
        </>
      )}
    </div>
  );
}

export default JokeList;
